import React from 'react';
import Modal from '../../../../components/modales/modal';
import { Form, FieldInput, Label } from '../../../../components/inputs/item-forms';
import { Input } from '../../../../components/inputs/inputs';
import { ButtonAction } from '../../../../components/buttons/button-action';
import { connect } from 'react-redux'
import SpinerForm from '../../../../components/spiners/spiner-form';
import { PutMethod } from '../../../../utils/peticiones';
import { ErrorAlert, SuccesAlert, InfoAlert } from '../../../../utils/alertas';


const EditSection = (props) => {

    //console.log('EDITAR', props)

    const {menu} = props.match.params
    const token = props.sesion.token
    const [modal, setModal] = React.useState(false)
    const [cargando, setCargando] = React.useState(true)
    const [name, setName] = React.useState('')
    const [load, setLoad] = React.useState(false)

    React.useEffect(()=>{  
        setTimeout(() =>{ setModal(true) },100)
        if(props.sections.length !== 0){
            const secciones = props.sections
            const mapSecciones = secciones.map((seccion) => seccion.id_section)
            const seleccionada = parseInt(props.match.params.section)
            const indexSeleccionado = mapSecciones.indexOf(seleccionada)
            const elemento = secciones[indexSeleccionado]
            setName(elemento.name)
            setCargando(false)
        }
    },[props])

    const openClose = () =>{
        setModal(!modal)
        props.history.push(`/dashboard/menu/${menu}`)
    }
    const closeModal = () =>{
        setModal(false)
        props.history.push(`/dashboard/menu/${menu}`)
    }

    const handleSubmit = async (e) =>{
        e.preventDefault();
        setLoad(true)
        const secciones = props.sections
        const mapSecciones = secciones.map((seccion)=> seccion.id_section)
        const seleccionada = parseInt(props.match.params.section)
        const index = mapSecciones.indexOf(seleccionada)
        const item = secciones[index]

        if(name === ''){
            InfoAlert('El campo de nombre no puede estar vacio')
            setLoad(false)
            return;
        }

        const editSection={
             name: name,
             id_section: item.id_section,
             index: item.index
        }

        const result = await PutMethod(editSection, `api/users/section/${item.id_section}`, {Authoritazion: 'Bearer ' + token})
        if(result.ok){
            secciones.splice(index, 1, result.response.data.data );
            props.clearSections();
            props.putSections(secciones);
            setLoad(false)
            SuccesAlert('Secci贸n actualizada')
            props.history.push(`/dashboard/menu/${menu}`)
        }else{
            ErrorAlert('Algo salio mal')
            setLoad(false)
        }
    }




    if(cargando){
        return <SpinerForm/>
    }

    return (
        <>  
        <Modal toggle={modal} toggleModal={openClose} cerrarFunction={closeModal}>
            <Form onSubmit={handleSubmit}>

                <FieldInput>
                    <h1 style={{    color:'var(--colordos)',
                                    fontSize:'1.5rem',
                                    fontFamily:'Bold',
                                    textAlign:'center'
                                    }}>Editar secci贸n</h1>

                </FieldInput>
                <FieldInput>
                    <Label>Editar nombre de secci贸n</Label>
                    <Input name="name" value={name} onChange={(e) =>{setName(e.target.value)}}/>
                </FieldInput>
                <FieldInput>
                    {load ?
                        <SpinerForm/>
                        :
                        <ButtonAction   maxWidth="100%">Editar</ButtonAction>
                    }
                </FieldInput>

            </Form> 
        </Modal>
        </>
    );
};


//---------REDUX
const mapStateToProps = (state) => {
    return {
        sections: state.menuSection,
        sesion: state.login
    }
}
const mapDispatchToProps = (dispatch) => {
    return {
        putSections: (data) => {
            dispatch({type:'EDITAR_SECTION', data:data})
        },
        clearSections: () => {
            dispatch({type:'CLEAN_SECTIONS'})
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(EditSection)
