import React from 'react';
import { PDFViewer,Page,Document, StyleSheet, View } from '@react-pdf/renderer'
import { GetMethod } from '../../../utils/peticiones';
import SpinerCharge from '../../../components/spiners/spiner-charge';
import QrRenderItems from './items/qr-render-items';
import {connect} from 'react-redux'
import QrRenderItemsAlt from './items/qr-render-items-alt';


const styles = StyleSheet.create({
    view:{width:'100%', height:'100vh'},
    container:{
        flexDirection:'row',
        flexWrap:'wrap',
        justifyContent:'center'
    }
})


const QrBranchoffice = (props) => {

    const ideSucursal = props.match.params.id
    const token = props.sesion.token

    const [load, setLoad] = React.useState(true)
    const [data, setData] = React.useState([])

    React.useEffect(() =>{
        const getQrs = async () =>{
            const result = await GetMethod(`api/users/restaurant/tables/${ideSucursal}`, {Authorization:'Bearer ' + token})
            console.log(result.response.data.data)
            if(result.ok){
                setData(result.response.data.data)
                setLoad(false)
            }else{
                setLoad(false)
            }
        }
        getQrs();
    },[ideSucursal, token])

    //console.log(data.tables)

    if(load){
        return <SpinerCharge/>
    }else{


        return (
            <PDFViewer style={styles.view}>
            <Document>
            <Page size="A4">
                <View style={styles.container}>
                    {/* <QrRenderItems {...props} data={data}/> */}
                    <QrRenderItemsAlt {...props} data={data}/>
                </View>
            </Page>
            </Document>
            </PDFViewer>
        );



    }
};

//REDUX
const mapStateToProps = (state) => {
    return {
        sesion: state.login
    }
}
export default connect(mapStateToProps)(QrBranchoffice)
//export default QrBranchoffice;
