import React from 'react';
import { Form, FieldInput, Label } from '../../../../../components/inputs/item-forms';
import { Input } from '../../../../../components/inputs/inputs';
import { ButtonAction } from '../../../../../components/buttons/button-action';





const FormSection = (props) => {
    //console.log(props)
    return (
        <Form>
            <FieldInput>

                <h1 style={{    color:'var(--colordos)',
                                fontSize:'1.5rem',
                                fontFamily:'Bold',
                                textAlign:'center'
                                }}>Crear sección</h1>

            </FieldInput>
            <FieldInput>
                <FieldInput>
                    <Label>Nombre de la sección</Label>
                    <Input  name="nameSection"
                            value={props.nameSection}
                            onChange={props.onChange}
                            placeholder="Escribe el nombre de la sección"
                            />
                </FieldInput>
                <FieldInput>
                    <ButtonAction onClick={(e) =>{
                        e.preventDefault();
                        props.addSection()
                        }} maxWidth="100%">Agregar</ButtonAction>
                </FieldInput>
            </FieldInput>
        </Form>
    );
};

export default FormSection;
