import React from 'react';
import { GetMethod, PostMethod } from '../../../../utils/peticiones';
import SpinerCharge from '../../../../components/spiners/spiner-charge';
import { WidthCaja, ItemCaja, CenterItem } from '../../../../components/containers/containers';
import {Link} from 'react-router-dom'
import {connect} from 'react-redux'
import { FieldInput, Label } from '../../../../components/inputs/item-forms';
import { SuccesAlert, ErrorAlert } from '../../../../utils/alertas';
import { InputSelectApi } from '../../../../components/inputs/input_select/inputselect_api';
import { QrContainer } from '../styles/branch-styles';
import ReactTooltip from 'react-tooltip'



const DataSucursal = (props) => {



    const token = props.sesion.token
    const ideSucursal = props.match.params.id

    const [load, setLoad] = React.useState(true)
    const [item, setItem] = React.useState([])
    const [menus, setMenus] = React.useState([])



    React.useEffect(()=>{
        const getSucursal = async () =>{
            const result = await GetMethod(`api/users/restaurant/${ideSucursal}`, {Authorization:'Bearer ' + token})
            if(result.ok){
                setItem(result.response.data.data)
                setMenus(result.response.data.data.user_menus)
                setLoad(false)
            }else{
                setLoad(false)
                console.log(result.response)
            }
        }
        getSucursal();
    },[ideSucursal, token])

    const handleMenu = async (e) => {

        const assingNew = {
            id_menu:e.target.value,
            id_restaurant:ideSucursal
        }
        const result = await PostMethod(assingNew, `api/users/restaurant/set_menu`, {Authorization:'Bearer ' + token})
        if(result.ok){
            SuccesAlert('Menu asignado')
        }else{
            ErrorAlert('Algo salio mal')
        }
    }





    if(load){

        return <SpinerCharge/>

    }else{

        return (

            <WidthCaja flow="column" maxWidth="400px">


                <ItemCaja >
                <section className="title">
                    <div className="left" data-tip data-for="suc-title" style={{cursor:'help'}}>
                        <div>
                            <span><i className="fas fa-store"></i></span>
                        </div>
                        <div>
                            <h1>{item.name}</h1>
                            <ReactTooltip id="suc-title">{item.name}</ReactTooltip>
                        </div>
                    </div>
                    <div className="right">
                        <Link to={`/dashboard/sucursal/${ideSucursal}/editar`}>
                            Editar
                            <i className="fas fa-edit"></i>
                        </Link>
                    </div>
                </section>
                </ItemCaja>


                <ItemCaja>

                        <FieldInput>
                            <Label>Asigna un menu</Label>

                            <InputSelectApi>
                            <span><i className="fas fa-caret-down"></i></span>
                            <select name="menu" onChange={handleMenu}>

                                <option         value={item.menu.length === 0 ? 'Asigna un menu' : 'Menu asignado'}>
                                                {item.menu.length === 0 ? 'Asigna un menu' : `Menu Actual: ${item.menu[0].name}`} </option>
                                                {menus.map((menu, i) =>{
                                                    return  <option     value={menu.id_menu}
                                                                        key={i}
                                                                        >{menu.name}</option>
                                                })}

                            </select>
                            </InputSelectApi>

                        </FieldInput>


                        <section style={{widht:'100%', display:'flex', justifyContent:'flex-end', padding:'10px 0px'}}> 
                                <Link to="/dashboard/menus" style={{textDecoration:'none'}}>
                                    Crear nuevo menu
                                    <i className="fas fa-plus-circle" style={{padding:'0px 5px'}}></i>
                                </Link>
                        </section>

                </ItemCaja>


                <ItemCaja>
                        <FieldInput>

                        <Label>Codigos QR</Label>
                        <QrContainer>
                            <div>
                                <p>
                                Descarga e imprime  los QR´s para las mesas
                                ponlos  en tus mesas en un lugar visible para que los que
                                los clientes escanen el código y visualicen el menú.
                                </p>
                            </div>
                            <div>
                            <Link   to={`/dashboard/sucursal/${ideSucursal}/qr`}>
                                    Ver pdf
                                    <i className="fas fa-qrcode"></i>
                            </Link>

                            </div>
                        </QrContainer>
                        {/* <CenterItem>
                            <Link   to={`/dashboard/sucursal/${ideSucursal}/qr`}
                                    >
                                <i className="fas fa-qrcode"
                                    style={{fontSize:'5rem'}}
                                ></i>
                            </Link>
                        </CenterItem> */}

                        </FieldInput>
                </ItemCaja>
            </WidthCaja>

        );
    }
};




//REDUX------------>

const mapStateToProps = (state) => {
    return {
        sesion: state.login
    }
}


export default connect(mapStateToProps)(DataSucursal)
