//
//-----> AQUI SE FILTRAN LOS PRODUCTOS DE CADA SECCION
//------------------------------------------------->
import React from 'react';
import EcardProducto from './e-card-producto';


const EvistaProductos = (props) => {


    const {id_section} = props.section
    const [productos, setProductos] = React.useState([])

    React.useEffect(()=>{
        const render = props.products.filter(p => p.section_id === id_section)
        setProductos(render)
    },[props,id_section])

    //console.log(productos)



    return (
        <>
            {
                productos.map((producto, i) =>{
                    return <EcardProducto   key={i}
                                            producto={producto}
                                            {...props}
                                            />
                })
            }
        </>
    );
};

export default EvistaProductos;
