import React from 'react';
import { Image, View,Text, StyleSheet } from '@react-pdf/renderer'



const styles = StyleSheet.create({
    item:{

        width:'10cm',
        height:'14cm',
        flexDirection:'column',
        marginTop:'10px',
        justifyContent:'center',
        borderWidth:1,
        borderColor:'#ccc',
        borderStyle:'dotted',
        position:'relative'
    },
    number:{
        position:'absolute',
        right:0,
        top:0,
        padding:'10px 5px'
    },
    qrCont:{

        flexDirection:'row',
        justifyContent:'center',
        width:'10cm',
        padding:'10px 0px'
    },
    txtMenu:{
        width:'10cm',
        flexDirection:'row',
        justifyContent:'center',
        padding:'5px 0px',
    },
    txtTitle:{
        fontWeight:4,
        width:'10cm',
        flexDirection:'column',
        padding:'5px 0px'
    },
    left:{
        width:'10cm',
        flexDirection:'row',
        justifyContent:'center'
    },
    right:{

        width:'10cm',
        flexDirection:'column',
        justifyContent:'center',
        alignItems:'center',
        padding:'10px'
    },
    img:{
        width:'60px',
        borderBottomRightRadius:100,
        borderTopRightRadius:100,
        borderBottomLeftRadius:100,
        borderTopLeftRadius:100,
    },
    footer:{

        padding:'10px 0px',
        flexDirection:'row',
        justifyContent:'center'
    },
    txtName:{
        color:'#7417ff',
        fontSize:15,
        textAlign:'justify',
        flexWrap:'wrap'
    },
    txtSubtitle:{
        color:'#111',
        fontSize:12,
        padding:'5px'
    },
    txtFooter:{
        color:'#7417ff',
        fontSize:12
    }
})


const QrRenderItems = (props) => {

    const {data} = props
    const {tables} = props.data
    //console.log(data.name.split(' ')[0])

    return (
        <>
        {
            tables.map((table,i) =>{
                const indice = i + 1
                if(indice % 4 === 1 && indice !== 1){

                    return  <View break style={styles.item} key={i}>
                                <View style={styles.number}>
                                    <Text style={{fontSize:12, color:'#111'}}>{table.number}</Text>
                                </View>


                                <View style={styles.txtTitle}>
                                    {/* <View style={styles.left}>
                                        <Image src={data.url_image} style={styles.img}/>
                                    </View> */}

                                    <View style={styles.right}>
                                        <Text style={styles.txtName}>{data.name}</Text>
                                        <Text style={styles.txtSubtitle}>categoria</Text>
                                    </View>

                                </View>



                                <View style={styles.txtMenu}>
                                    <Text style={{fontSize:12, color:'#7417ff'}}>Escanea para ver el menu</Text>
                                </View>
                                <View style={styles.qrCont}>
                                    <Image src={table.qrcode} style={{width:'150px'}}/>
                                </View>

                                <View style={styles.footer}>
                                    <Text style={styles.txtFooter}>www.popadvise.com</Text>
                                </View>
                            </View>


                }else{

                    return  <View style={styles.item} key={i}>
                                <View style={styles.number}>
                                    <Text style={{fontSize:12, color:'#111'}}>{table.number}</Text>
                                </View>


                                <View style={styles.txtTitle}>
                                    {/* <View style={styles.left}>
                                        <Image src={data.url_image} style={styles.img}/>
                                    </View> */}

                                    <View style={styles.right}>
                                            <Text style={styles.txtName}>{data.name}</Text>
                                            <Text style={styles.txtSubtitle}>categoria</Text>
                                    </View>
                                </View>



                                <View style={styles.txtMenu}>
                                    <Text style={{fontSize:12, color:'#7417ff'}}>Escanea para ver el menu</Text>
                                </View>
                                <View style={styles.qrCont}>
                                    <Image src={table.qrcode} style={{width:'150px'}}/>
                                </View>

                                <View style={styles.footer}>
                                    <Text style={styles.txtFooter}>www.popadvise.com</Text>
                                </View>
                            </View>


                }
            })
        }

        </>
    );
};

export default QrRenderItems;
