//ESte componente redirecciona si no hay sucursales creadas
//------>
//------>
//------>
import React from 'react';
import { connect } from 'react-redux';
import {Redirect} from 'react-router-dom'
import MenuVista from './items/menu-vista';
import { CenterTop } from '../../../components/containers/containers';



const Menus = (props) => {

    //console.log('%c->MENUS', 'color:peru', props)


    if(props.sucursales.length === 0){
        return <Redirect to="/dashboard/first"/>
    }

    return (
        <CenterTop>
            <MenuVista/>
        </CenterTop>

    );
};




//--------->REDUX


const mapStateToProps = (state) => {
    return {
        sucursales: state.sucursales
    }
}
export default connect(mapStateToProps)(Menus)
