import React from 'react';
import { Form, FieldInput, Label } from '../../../../../components/inputs/item-forms';
import { Input, TextArea } from '../../../../../components/inputs/inputs';
import { ButtonAction } from '../../../../../components/buttons/button-action';
import { connect } from 'react-redux'
import InputFile from '../../../../../components/inputs/input_file/input_file';
import uuid from 'react-uuid'
import SpinerForm from '../../../../../components/spiners/spiner-form';


const FormItem = (props) => {
    //console.log('LAFIONA', props)
    const {name, price, description} = props.item
    const {setItem} = props
    const {item} = props
    const {addItem} = props
    const {image, imageUrl} = props.image


    //console.log('CAMP', image, imageUrl)
    const onDrop = (e) =>{
        e.preventDefault();
        const file = e.dataTransfer.files[0]

        console.log(file)
        let reader = new FileReader();
        reader.readAsDataURL(file)
        reader.onload = function(){
            props.setImage({...image, imageUrl:reader.result, image:file})
        }

    }

    const onDragOver = (e) => e.preventDefault();

    const handleFile = (e) =>{
        if(e.target.files && e.target.files[0]){
            const file = e.target.files[0]
            //console.log(file)

            let reader = new FileReader();
            reader.readAsDataURL(file)
            reader.onload = function(){
                props.setImage({...image, imageUrl:reader.result, image:file})
            }

        }
    }



    return (

        <Form>
            <FieldInput>

                    <h1 style={{    color:'var(--colordos)',
                                    fontSize:'1.5rem',
                                    fontFamily:'Bold',
                                    textAlign:'center'
                                    }}>Crear producto</h1>

            </FieldInput>
            <FieldInput>
                        <Label>Nombre del producto:</Label>
                        <Input  type="text"
                                name="name"
                                value={name}
                                placeholder="Asigna un nombre"
                                onChange={(e) => setItem({...item, [e.target.name]: e.target.value})}/>
                    </FieldInput>
                    <FieldInput>
                        <Label>Precio:</Label>
                        <Input  type="text"
                                name="price"
                                value={price}
                                placeholder="Asigna un precio"
                                onChange={(e) => setItem({...item, [e.target.name]: e.target.value})}/>
                    </FieldInput>
                    <FieldInput>
                        <Label>Descripci√≥n</Label>
                        <TextArea
                            name="description"
                            value={description}
                            placeholder="Descripcion"
                            onChange={(e) => setItem({...item, [e.target.name]: e.target.value})}
                        ></TextArea>
                    </FieldInput>


                    <FieldInput>
                        <Label>Sube o arrastra una imagen</Label>


                        <InputFile  ide={`p-image${uuid()}`}
                                    onDrop={onDrop}
                                    onDragOver={onDragOver}
                                    imgPrevio={imageUrl}
                                    onChange={handleFile}
                                    width="100%"
                                    />



                    </FieldInput>




                    <FieldInput>
                        {props.send ? 
                            <SpinerForm/>
                            :
                            <ButtonAction onClick={(e) =>{
                                e.preventDefault();
                                addItem();
                            }} maxWidth="100%"> Agregar item</ButtonAction>
                        }
                    </FieldInput>


        </Form>


    );
};





//REDUX-------------------->

const mapStateToProps = (state) => {
    return {
        objects: state.menuObject
    }
}
export default connect(mapStateToProps)(FormItem)
