import React from 'react';
import { Form, FieldInput, Label } from '../../../../../components/inputs/item-forms';
import { Input, TextArea } from '../../../../../components/inputs/inputs';
import { ButtonAction } from '../../../../../components/buttons/button-action';
import InputFile from '../../../../../components/inputs/input_file/input_file';
import uuid from 'react-uuid'
import SpinerForm from '../../../../../components/spiners/spiner-form';

const PutFormItem = (props) => {

    const {dataitem, handleChange, handleSubmit, handleFile, onDragOver, onDrop } = props
    const {name, price, description, imageUrl} = dataitem
    //console.log('ITEMPUT',image)






    return (
        <Form>
            <FieldInput>

                <h1 style={{    color:'var(--colordos)',
                                fontSize:'1.5rem',
                                fontFamily:'Bold',
                                textAlign:'center'
                                }}>Editar producto</h1>

            </FieldInput>

            <FieldInput>
                <Label>Editar nombre:</Label>
                <Input  type="text"
                        value={name}
                        onChange={handleChange}
                        name="name"
                        />
            </FieldInput>

            <FieldInput>
                <Label>Editar precio:</Label>
                <Input  type="text"
                        value={price}
                        onChange={handleChange}
                        name="price"
                        />
            </FieldInput>

            <FieldInput>
                <Label>Editar descripci√≥n</Label>
                <TextArea   value={description}
                            onChange={handleChange}
                            name="description"
                            ></TextArea>
            </FieldInput>


            <FieldInput>
                <InputFile  ide={`e-img${uuid()}`}
                            onDrop={onDrop}
                            onDragOver={onDragOver}
                            imgPrevio={imageUrl}
                            onChange={handleFile}
                            />
            </FieldInput>



            <FieldInput>
                {props.cargando ? 
                    <SpinerForm/>
                    :
                    <ButtonAction   maxWidth="100%"
                                onClick={(e) =>{
                                    e.preventDefault();
                                    handleSubmit();
                                }}
                                >Editar</ButtonAction>
                }

            </FieldInput>


        </Form>
    );
};

export default PutFormItem;
