import React from 'react';
import Sucursal from './components/sucursal';
import { Container } from './styles/contenedores';
import UserBartop from './components/user-bartop';
import { GetMethod } from '../utils/peticiones';
import { ErrorAlert } from '../utils/alertas';
import SpinerCharge from '../components/spiners/spiner-charge';

const VistaUsuario = (props) => {


    //console.log(props.location.pathname)
    //TO DO : Aqui hay que hacer la peticion
    const [data, setData] = React.useState([])
    const [busqueda, setBusqueda] = React.useState('')
    const [load, setLoad] = React.useState(true)

    React.useEffect(() =>{
        const getMenu = async () =>{
            const result = await GetMethod(`api${props.location.pathname}`)
            //console.log(result)
            if(result.ok){
                setData(result.response.data)
                //console.log('RESULTADO PETICION',result.response.data.menu)
                setLoad(false)
            }else{
                ErrorAlert('Algo salio mal')
                setLoad(false)
            }
        }
        getMenu();  
    },[props])

    //console.log('RESULTADO FILTRO', data.menu) 

    if(load){
        return <SpinerCharge/>
    }else{

        return (
            <Container>


                <UserBartop {...props} data={data}/>
                <Sucursal   {...props} data={data} setBusqueda={setBusqueda} busqueda={busqueda}/> 


            </Container>

        );


    }


};

export default VistaUsuario;
