import React from 'react';
import SubtitleMenu from './items/subtitle-menu';
//import uuid from 'react-uuid'
import { ButtonAction } from '../../../components/buttons/button-action';
import { ContainerMenu } from './styles/menu-styles';
import Modal from '../../../components/modales/modal';
import Section from './items/section';
//import VacioItem from '../../../components/titles/vacio-item';
//import { InfoAlert } from '../../../utils/alertas';
import FormSection from './items/forms/form-section';
import { PostMethod } from '../../../utils/peticiones';
import { connect } from 'react-redux'
import { SuccesAlert, ErrorAlert, InfoAlert } from '../../../utils/alertas';


const initialState = {
    name:'',
    rows:{},
    items:{},
    rowsOrder:[],
    nameSection:'',
    sections:[], 
    products:[],
}


const CreateMenu = (props) => {
    //console.log('CREAR MENU',props)

    //console.log(props.section)

    const token = props.sesion.token

    const [data, setData] = React.useState(initialState)
    const [togglemodal, setTogglemodal] = React.useState(false)
    //const [togglemodalitem, setTogglemodalitem] = React.useState(false)



    const showModal = () => setTogglemodal(!togglemodal)

    const sectionChange = (e) =>{
        setData({
            ...data,
            [e.target.name]:e.target.value
        })
    }



    const handleSave = () =>{}
    const {cleanMenu, cleanProducts, cleanSections} = props
    React.useEffect(() =>{ return () =>{
                                            cleanMenu()
                                            cleanSections()
                                            cleanProducts()
                                        } },[cleanMenu, cleanProducts, cleanSections])





    // FUNCION DE AGREGAR SECCION
    const addSection = async () =>{

        const nameSection = data.nameSection
        //const nameSectionId = data.nameSection + uuid();
        if(nameSection === ''){
            InfoAlert('LLena los campos')
            return;
        }
        const stateSections = data.sections
        stateSections.push(nameSection)
        const index = stateSections.indexOf(nameSection)
        const ideMenu = props.menu.id_menu
        const newSection={
            name:nameSection,
            index:index,
            id_menu:ideMenu
        }
        const newRow = {
            ...data.rows,
            [nameSection]:{
                ...data.rows[nameSection],
                items:[]
            }
        }


        const result = await PostMethod(newSection, 'api/users/section', {Authorization:'Bearer ' + token})
        if(result.ok){
            //console.log('VICENTE FOX',result.response.data)
            props.addSection(result.response.data)
            showModal()
            setData({
                ...data,
                nameSection:'',
                rows:newRow
            })
            SuccesAlert('Seccion creada')
        }else{
            ErrorAlert('Algo salio mal')
        }

    }

    return (
        <>
            {/* MODAL DE AGREGAR SECCION */}
            <Modal toggle={togglemodal} toggleModal={showModal}>
                <FormSection    nameSection={data.nameSection}
                                onChange={sectionChange}
                                addSection={addSection}
                                />
            </Modal>
            {/* MODAL DE AGREGAR SECCION */}


            {/* CAMBIAR EL NOMBRE DEL MENU */}

                <SubtitleMenu data={data} setData={setData} handleSave={handleSave} {...props}/>

            {/* CAMBIAR EL NOMBRE DEL MENU */}

                <ContainerMenu>
            {/* ESTE ES EL CONTENEDOR DEL TABLERO DE MENU */}
            {/* AQUI SE RENDERIZAN LAS SECCIONES */}





            {props.section.map((sect, i) =>{
                return <Section key={i} sect={sect} data={data} setData={setData}/>
            })}





            {/* AQUI SE RENDERIZAN LAS SECCIONES */}
            {/* ESTE ES EL CONTENEDOR DEL TABLERO DE MENU */}

                <section  className="bottom-button">
                <ButtonAction   icon
                                maxWidth="250px"
                                onClick={showModal}
                                disabled={data.name ? false : true}
                                style={{
                                    background: data.name  ? 'var(--base)' : '#ccc'
                                }}
                                >
                    Crear nueva secci√≥n
                    <i className="fas fa-plus-circle"></i>
                </ButtonAction>
                </section>
            {/* ESTE ES EL CONTENEDOR DEL TABLERO DE MENU */}
            </ContainerMenu>


        </>
    );
};





//REDUX ------------------>


const mapStateToProps = (state) => {
    return {
        sesion: state.login,
        menu: state.menu,
        section: state.menuSection
    }
}
const mapDispatchToProps = (dispatch) => {
    return {
        cleanMenu: () => {
            dispatch({ type:'REMOVE_MENU' })
        },
        addSection: (data) =>{
            dispatch({type:'ADD_SECTION', data:data})
        },
        cleanSections:() =>{
            dispatch({type:'CLEAN_SECTIONS'})
        },
        cleanProducts:() =>{
            dispatch({type:'CLEAN_OBJECT'})
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CreateMenu)
