import React from 'react';
import Modal from '../../../../components/modales/modal';
import { Form, FieldInput, Label } from '../../../../components/inputs/item-forms';
import { Input, TextArea } from '../../../../components/inputs/inputs';
import InputFile from '../../../../components/inputs/input_file/input_file';
import uuid from 'react-uuid'
import {connect} from 'react-redux'
import { PostMethod } from '../../../../utils/peticiones';
import { ButtonAction } from '../../../../components/buttons/button-action';
import { ErrorAlert, SuccesAlert, InfoAlert } from '../../../../utils/alertas';
import SpinerForm from '../../../../components/spiners/spiner-form';



const CreateProduct = (props) => {
    console.log('CREAR',props)

    const token = props.sesion
    const [modal, setModal] = React.useState(false)
    const [send, setSend] = React.useState(false)
    const [producto, setProducto] = React.useState({    image:null, urlImage:'',name:'',
                                                        price:'', description:''
                                                    })

    React.useEffect(()=>{

        setTimeout(()=>{setModal(true)},100)

        if(props.match.params.product){

            const idProduct = parseInt(props.match.params.product)
            const productos = props.products

            if(productos.length !== 0){
                const mapProductos = productos.map((producto) => producto.id_product)
                const indexSelect = mapProductos.indexOf(idProduct)
                const obj = productos[indexSelect]
                console.log(obj)
                setProducto({
                    ...producto,
                    urlImage:obj.url_image,
                    name:obj.name,
                    price:obj.price,
                    description:obj.description
                })
            }

        }else{
            console.log('no existe el producto')
        }
    },[props])

    const openClose = () =>{
        setModal(!modal)
        props.history.push(`/dashboard/menu/${props.match.params.menu}`)
    }

    const closeModal = () =>{
        setModal(false)
        props.history.push(`/dashboard/menu/${props.match.params.menu}`)
    }

    const onDrop = (e)=>{
        e.preventDefault();
        const file = e.dataTransfer.files[0]

        let reader = new FileReader();
        reader.readAsDataURL(file)
        reader.onload = function(){
            setProducto({
                ...producto,
                image: file,
                urlImage: reader.result
            })
        }
    }

    const onDragOver = (e) =>{e.preventDefault()}

    const handleFile = (e) =>{
        if(e.target.files && e.target.files[0]){
            const file = e.target.files[0]

            let reader = new FileReader();
            reader.readAsDataURL(file)
            reader.onload = function(){
                setProducto({
                    ...producto,
                    image: file,
                    urlImage: reader.result
                })
            }
        }
    }

    const handleChange = (e) =>{
        setProducto({
            ...producto,
            [e.target.name] : e.target.value
        })
    }

    const handleSubmit = async (e) =>{
        e.preventDefault();
        setSend(true)

        const productos = props.products
        const filterProducts = productos.filter((pr) => pr.section_id === parseInt(props.match.params.section))
        const index = filterProducts.length

        if(props.match.params.product){

            const mapProducts = productos.map((producto) => producto.id_product)
            const ideSelected = parseInt(props.match.params.product)
            const indexSelected = mapProducts.indexOf(ideSelected)

            if( producto.name === '' || producto.description === '' || producto.price === ''){
                InfoAlert('LLena todos los campos')
                setSend(false)
                return;
            }

            const formData = new FormData();
            formData.append('name', producto.name)
            formData.append('price', producto.price)
            formData.append('description', producto.description)
            formData.append('id_section', props.match.params.section)
            formData.append('index', index)
            formData.append('_method', 'PUT')
            formData.append('image', producto.image)
            const result = await PostMethod(formData, `api/users/product/${props.match.params.product}`, {Authorization:'Bearer ' + token})
            if(result.ok){
                //console.log(result.response.data)
                productos.splice(indexSelected, 1 , result.response.data)
                props.cleanProduct();
                props.refreshProduct(productos)
                SuccesAlert('Producto actualizado')
                setSend(false)
                props.history.push(`/dashboard/menu/${props.match.params.menu}`)
            }else{
                ErrorAlert('Algo salio mal')
                setSend(false)
                props.history.push(`/dashboard/menu/${props.match.params.menu}`)
            }

            return;
        }
        if(producto.image === null || producto.name === '' || producto.description === '' || producto.price === ''){
            InfoAlert('LLena todos los campos')
            setSend(false)
            return;
        }

        const formData = new FormData();
        formData.append('name', producto.name)
        formData.append('price', producto.price)
        formData.append('description', producto.description)
        formData.append('id_section', props.match.params.section)
        formData.append('index', index)
        formData.append('image', producto.image)



        const result = await PostMethod(formData, `api/users/product`, {Authoritazion:'Bearer ' +  token})
        if(result.ok){
            console.log(result.response.data)
            props.addProduct(result.response.data)
            SuccesAlert('Producto creado')
            setSend(false)
            props.history.push(`/dashboard/menu/${props.match.params.menu}`)
        }else{
            ErrorAlert('Algo salio mal')
            setSend(false)
            props.history.push(`/dashboard/menu/${props.match.params.menu}`)
        }
    }



    return (
        <>
            <Modal toggle={modal} toggleModal={openClose} cerrarFunction={closeModal}>
                <Form onSubmit={handleSubmit}>


                    <FieldInput>
                        <h1 style={{    color:'var(--colordos)',
                                        fontSize:'1.5rem',
                                        fontFamily:'Bold',
                                        textAlign:'center'
                                        }}>Crear producto</h1>
                    </FieldInput>

                    <FieldInput>
                        <Label>Sube o arrastra una imagen</Label>


                        <InputFile  ide={`p-image${uuid()}`}
                                    onDrop={onDrop}
                                    onDragOver={onDragOver}
                                    imgPrevio={producto.urlImage}
                                    onChange={handleFile}
                                    width="100%"
                                    />



                    </FieldInput>






                    <FieldInput>
                        <Label>Nombre del producto:</Label>
                        <Input  type="text"
                                name="name"
                                value={producto.name}
                                placeholder="Asigna un nombre"
                                onChange={handleChange}/>
                    </FieldInput>

                    <FieldInput>
                        <Label>Precio:</Label>
                        <Input  type="number"
                                name="price"
                                value={producto.price}
                                placeholder="Asigna un precio"
                                onChange={handleChange}/>
                    </FieldInput>

                    <FieldInput>
                        <Label>Descripci√≥n</Label>
                        <TextArea
                            name="description"
                            value={producto.description}
                            placeholder="Descripcion"
                            onChange={handleChange}
                        ></TextArea>
                    </FieldInput>

                    <FieldInput>
                        {send ?
                            <SpinerForm/>
                            :
                            <ButtonAction maxWidth="100%"> Agregar item</ButtonAction>
                        }
                    </FieldInput>

                </Form>
            </Modal>
        </>
    );
};

//------>REDUX

const mapStateToProps = (state) => {
    return {
        products: state.menuProduct,
        sesion : state.login
    }
}
const mapDispatchToProps = (dispatch) => {
    return {
        addProduct: (data) => {
            dispatch({type:'ADD_PRODUCT', data:data})
        },
        refreshProduct: (data) => {
            dispatch({type:'EDITAR_PRODUCT', data:data})
        },
        cleanProduct: () => {
            dispatch({type:'CLEAN_PRODUCT'})
        },
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(CreateProduct)
