import React from 'react';
import { ContainerMenu } from '../styles/contenedores';
import UserMenuCategory from './user-menu-category';
import UserBuscador from './user-buscador';

//TO DO: hacer la peticion/ renderizar el menu



const UserMenu = (props) => {

    console.log('USER_MENU', props.m)

    const {sections, products} = props.m

    const[items, setItems] = React.useState(props.m.products)
    const [busqueda, setBusqueda] = React.useState('')

    const handleSearch = (e) =>{
        setBusqueda(e.target.value)
        const filtro = products.filter(
            (item) =>{
                return item.name.toLowerCase().includes(e.target.value.toLowerCase())
            }
        )
        setItems(filtro)
    }



    return (
        <>

        <UserBuscador setBusqueda={setBusqueda} busqueda={busqueda} handleSearch={handleSearch}/>

        <ContainerMenu>

            {
                sections.map((section, i) =>{
                    return <UserMenuCategory    key={i}
                                                category={section}
                                                products={items}
                                                />
                })
            }

        </ContainerMenu>
        </>
    );
};

export default UserMenu;
