import React from 'react';
import EcardSection from './e-card-section';
import { ButtonAction } from '../../../../../components/buttons/button-action';





const Esections = (props) => {

    //console.log('%c->PROPSections', 'color:sienna', props)
    const {sections} = props



    return (
        <>

            {sections.map((section,i) =>{
                return <EcardSection    key={i}
                                        section={section}
                                        {...props}
                                        />
            })}





            <section  className="bottom-button">
                <ButtonAction   icon
                                maxWidth="250px"
                                onClick={()=>{ props.history.push(`/dashboard/menu/${props.match.params.menu}/section`)}}
                                >
                                Crear nueva secci√≥n
                                <i className="fas fa-plus-circle"></i>
                </ButtonAction>
            </section>  




        </>
    );
};

export default Esections;
