import React from 'react';
import Modal from '../../../../components/modales/modal';
import { Form, FieldInput, Label } from '../../../../components/inputs/item-forms';
import { Input } from '../../../../components/inputs/inputs';
import { ButtonAction } from '../../../../components/buttons/button-action';
import {connect} from 'react-redux'
import { PostMethod } from '../../../../utils/peticiones';
import { ErrorAlert, SuccesAlert, InfoAlert } from '../../../../utils/alertas';
import SpinerForm from '../../../../components/spiners/spiner-form';


const CreateSection = (props) => {

    //name
    //index
    //id_menu

    //console.log('DESDE MODAL', props)
    const token = props.sesion.token
    const [modal, setModal] = React.useState(false)
    const [section, setSection] = React.useState('')
    const [btn, setBtn] = React.useState(false)

    React.useEffect(()=>{ setModal(true) },[])

    const closeModal = () => {
        setModal(false)
        props.history.push(`/dashboard/menu/${props.match.params.menu}`)
    }

    const openClose = () => {
        setModal(!modal)
        props.history.push(`/dashboard/menu/${props.match.params.menu}`)
    }

    const handleChange = (e) => setSection(e.target.value)

    const handleSubmit = async  (e) =>{
        setBtn(true)
        e.preventDefault();

        if(section === ''){
            InfoAlert('Llena el campo de nombre')
            setBtn(false)
            return;
        }

        const secciones = props.sections.length

        const newSection = {
            name: section,
            id_menu:props.match.params.menu,
            index:secciones
        }

        const result = await PostMethod(newSection, 'api/users/section', {Authorization:'Bearer ' + token})
        //console.log(result.response.data)
        if(result.ok){
            props.addSection(result.response.data)
            setBtn(false)
            SuccesAlert('Sección creada')
            props.history.push(`/dashboard/menu/${props.match.params.menu}`)
        }else{
            ErrorAlert('Algo salio mal')
            setBtn(false)
            props.history.push(`/dashboard/menu/${props.match.params.menu}`)
        }

    }   






    return (
        <>
            <Modal toggle={modal} toggleModal={openClose} cerrarFunction={closeModal}>

                <Form onSubmit={handleSubmit}>
                    <FieldInput>
                        <h1 style={{    color:'var(--colordos)',
                                        fontSize:'1.5rem',
                                        fontFamily:'Bold',
                                        textAlign:'center'
                                        }}>Crear sección</h1>

                    </FieldInput>


                    <FieldInput>

                        <FieldInput>
                            <Label>Nombre de la sección</Label>
                            <Input  name="nameSection"
                                    value={section}
                                    onChange={handleChange}
                                    placeholder="Escribe el nombre de la sección"
                                    />
                        </FieldInput>

                        <FieldInput>
                            {btn ? 
                                <SpinerForm/>
                                :
                                <ButtonAction maxWidth="100%">Agregar</ButtonAction>
                            }
                        </FieldInput>

                    </FieldInput>
                </Form>

            </Modal>   
        </>
    );
};






//---------->FUNCIONES DE REDUX

const mapStateToProps = (state) => {
    return {
        sections: state.menuSection,
        sesion: state.login
    }
}
const mapDispatchToProps = (dispatch) => {
    return {
        addSection: (data) => {
            dispatch({type:'ADD_SECTION', data:data})
        }
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(CreateSection)
