import React from 'react';
import { TitleForm } from '../../menus/styles/menu-styles';
import { FieldInput } from '../../../../components/inputs/item-forms';
import { Split } from '../../../../components/containers/containers';
import { ButtonAction } from '../../../../components/buttons/button-action';


const DeleteMenu = (props) => {
    return (
        <>
        <TitleForm>
                <h1>¿Desas borrar este menu?</h1>
                <p>Al eliminar este menu, se eliminará toda la información que contiene.</p>
            </TitleForm>

            <FieldInput>

                <section style={{width:'100%', maxWidth:'220px', margin:'auto'}}>
                    <Split>
                        <ButtonAction   onClick={props.deleteMenu}
                                        maxWidth="100px"
                        >Si</ButtonAction>

                        <ButtonAction   onClick={props.cerrarDelete}
                                        maxWidth="100px"
                        >Cancelar</ButtonAction>
                    </Split>
                </section>

            </FieldInput>

        </>
    );
};

export default DeleteMenu;
