import React from 'react';

const UserMenuProduct = (props) => {

    //console.log('PRODUCTO', props)
    const {name, description, price, url_image} = props.item

    return (
        <section className="caja">

            <section>
                <img src={url_image} alt="popadvise"/>
            </section>

            <section>

                <div><h1>{name}</h1></div>
                <div><p>{description}</p></div>

                <div><p>${price}</p></div>

            </section>

        </section>
    );
};

export default UserMenuProduct;
