import React from 'react';
import { ContainerSection } from '../../../menus/styles/menu-styles';
import { ButtonIcon } from '../../../../../components/buttons/button-icon';
import { ButtonAction } from '../../../../../components/buttons/button-action';
import { PostMethod } from '../../../../../utils/peticiones';
import BarCharge from '../../../../../components/spiners/bar-charge';
import EvistaProductos from './e-vista-productos';


const EcardSection = (props) => {

    //console.log('propsSections', props )
    const {name, id_section} = props.section
    const token = props.sesion.token 

    const [up, setUp] = React.useState(false)
    const [down, setDown] = React.useState(false)

    const openDelete = () =>{
        props.history.push(`/dashboard/menu/${props.match.params.menu}/section/${props.section.id_section}/delete`)
    }
    const openEdit = () =>{
        props.history.push(`/dashboard/menu/${props.match.params.menu}/section/${props.section.id_section}/editar`)
    }


    const upPosition = async () =>{
        setUp(true)
        const secciones = props.sections
        const seleccionadoId = props.section.id_section
        const mapSecciones = secciones.map((seccion) => seccion.id_section)
        const indexSeleccionado = mapSecciones.indexOf(seleccionadoId)

        if(indexSeleccionado === 0){
            console.log('primer elemento')
            const [select] = secciones.splice(indexSeleccionado, 1)
            secciones.push(select)

            const newSections = {
                id_menu:props.match.params.menu,
                sections: secciones
            }

            const result = await PostMethod(newSections, 'api/users/section/sort', {Authorization:'Bearer ' + token})
            if(result.ok){
                console.log(result.response.data);
                props.cleanSections();
                props.reorderSections(result.response.data);
                setUp(false)
            }else{
                console.log(result)
                setUp(false)
            }
            return;
        }


        const upSelected = secciones[indexSeleccionado -  1]

        const [select] = secciones.splice(indexSeleccionado, 1, upSelected)
        secciones.splice(indexSeleccionado - 1, 1, select)

        const newSections = {
            id_menu:props.match.params.menu,
            sections: secciones
        }


        const result = await PostMethod(newSections, 'api/users/section/sort', {Authorization:'Bearer ' + token})
        if(result.ok){
            console.log(result.response.data);
            props.cleanSections();
            props.reorderSections(result.response.data);
            setUp(false)
        }else{
            console.log(result)
            setUp(false)
        }


    }

    const downPosition = async () =>{
        setDown(true)
        const secciones = props.sections
        const seleccionadoId = props.section.id_section
        const mapSecciones = secciones.map((seccion) => seccion.id_section)
        const indexSeleccionado = mapSecciones.indexOf(seleccionadoId)

        if(indexSeleccionado === secciones.length -1){
            const [select] = secciones.splice(indexSeleccionado, 1)
            secciones.unshift(select)

            const newSections = {
                id_menu:props.match.params.menu,
                sections: secciones
            }

            const result = await PostMethod(newSections, 'api/users/section/sort', {Authorization:'Bearer ' + token})
            if(result.ok){
                console.log(result.response.data);
                props.cleanSections();
                props.reorderSections(result.response.data);
                setDown(false)
            }else{
                console.log(result)
                setDown(false)
            }
            return;
        }

        const downSelected = secciones[indexSeleccionado +  1]

        const [select] = secciones.splice(indexSeleccionado, 1, downSelected)
        secciones.splice(indexSeleccionado + 1, 1, select)

        const newSections = {
            id_menu:props.match.params.menu,
            sections: secciones
        }
        const result = await PostMethod(newSections, 'api/users/section/sort', {Authorization:'Bearer ' + token})
        if(result.ok){
            console.log(result.response.data);
            props.cleanSections();
            props.reorderSections(result.response.data);
            setDown(false)
        }else{
            console.log(result)
            setDown(false)
        }



    }

    const openProduct = () =>{
        ///dashboard/menu/:menu/section/:section/product
        props.history.push(`/dashboard/menu/${props.match.params.menu}/section/${id_section}/product`)
    }

    return (
        <>
        <ContainerSection>
            <section className="header">

                <div style={{color:'var(--colordos)'}}>
                    <i className="fas fa-columns" style={{  marginRight:'5px',
                                                            color:'var(--colordos)'
                                                        }}></i>
                                                        {name}
                </div>

                <div style={{display:'flex'}}>
                        <ButtonIcon  
                                    fontSize="1rem"
                                    backColor='var(--colorcuatro)'
                                    onClick={openEdit}
                                    color='var(--colordos)'
                                    ><i className="fas fa-edit"></i></ButtonIcon>

                        <ButtonIcon  
                                    fontSize="1rem"
                                    backColor='var(--colorcuatro)'
                                    onClick={openDelete}
                                    color='var(--colordos)'
                                    ><i className="fas fa-trash-alt"></i></ButtonIcon>



{/*----------> BOTONES PARA REORDENAR--------------------------------------------------> */}
                        {up ?
                            <BarCharge/>

                            :

                            <ButtonIcon  
                                        fontSize="1rem"
                                        backColor='var(--colorcuatro)'
                                        onClick={upPosition}
                                        color='var(--colordos)'

                                        ><i className="fas fa-chevron-circle-up"></i></ButtonIcon>
                        }
                        {down ?

                            <BarCharge/>

                            :

                            <ButtonIcon  
                                        fontSize="1rem"
                                        backColor='var(--colorcuatro)'
                                        onClick={downPosition}
                                        color='var(--colordos)'

                                        ><i className="fas fa-chevron-circle-down"></i></ButtonIcon>
                        }
                </div>

            </section>

            {/* AQUI RENDERIZAN LOS PRODUCTOS */}
            <section className="body">
                <EvistaProductos {...props}/>
            </section>
            {/* AQUI RENDERIZAN LOS PRODUCTOS */}

            <section className="btn-add">


                <ButtonAction onClick={openProduct} icon>
                    Añadir productos
                    <i className="fas fa-plus-circle"></i>
                </ButtonAction>


            </section>

        </ContainerSection>

        </>
    );
};


//--------> REDUX

export default EcardSection
