import React from 'react';
import UserMenuProduct from './user-menu-product';

const UserMenuCategory = (props) => {

    const {name, id_section} = props.category
    const products = props.products

    const sectionFilter = products.filter(prd => prd.section_id === id_section)



    return (
        <>
        <section className="category">
            <h1>{name}</h1>
        </section>

        <section className="product">
           {
                sectionFilter.map((item, i) =>{
                 return <UserMenuProduct  key={i}
                                          item={item}
                                          />
                })
           } 
        </section>

        </>
    );
};

export default UserMenuCategory;
