//
//--->Vista padre de editar menu 
//--->aqui evalua que haya sucursales creadas
//
import React from 'react';
import {connect} from 'react-redux'
import {Redirect} from 'react-router-dom'
import { CenterTop } from '../../../components/containers/containers';
import Emenu from './vistas/e-menu';
import {Switch, Route} from 'react-router-dom'



const MenuEditar = (props) => {

    //Declarar variables
    //console.log('%c-> PROPS', 'color:tomato', subroutes)
    const {sucursales} = props
    const {subroutes} = props

    React.useEffect(() =>{
        return () =>{

            props.clearMenu()
            props.clearProducts()
            props.clearSections()
        }
    },[])


    if(sucursales.length === 0){

        return <Redirect to="/dashboard/first"/>

    }

    return (
        <>
            <CenterTop> <Emenu {...props}/> </CenterTop>
            <Switch>
            {
                subroutes.map((subroutes, i) =>{
                    return <Route   key={i}
                                    path={subroutes.path}
                                    exact={subroutes.exact}
                                    render={(props) => {
                                        return <subroutes.component {...props}/>
                                    }}
                                    />        
                })
            }

            </Switch>
        </>
    );
};












// FUNCIONES DE REDUX
const mapStateToProps = (state) => {
    return {
        sucursales: state.sucursales,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        clearMenu: () => {
            dispatch({type:'REMOVE_MENU'})
        },
        clearSections: () => {
            dispatch({type:'CLEAN_SECTIONS'})
        },
        clearProducts: () => {
            dispatch({type:'CLEAN_PRODUCT'})
        },
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(MenuEditar)
